import * as api from './api'

export function register (data) {
  return api.post('/register', data)
}

export function login (data) {
  return api.post('/login', data)
}


export function getUser (data) {
  console.log("GETUSER:" + JSON.stringify(data));
  return api.get(`/users/${data.userid}`)
}

export function getUserStats(data) {
  console.log("GETUSERSTATS:" + JSON.stringify(data));
  return api.get(`/users/${data.user._id}/stats/${data.daterange}`)
}