export function post(endpoint, data) {
	console.log("^^^^^^^^^^^^^^^^^^^ inside utils Post" + endpoint + JSON.stringify(data));
	return fetch(endpoint, {
		method: 'POST',
		credentials: 'include',
		body: JSON.stringify(data),
		headers: {
			'Content-Type': 'application/json'
		}
	}).then(r => r.json());
}


